import React, {Component} from 'react';
import '../stylesheet/mainStyle.css';
import UserThreads from './userThreads';
import ChatHeader from './chatHeader';
import MessageBody from './messageBody';
class Main extends Component{
    state = {
        userArr : [
            {id:1,name:"User #1"},
            {id:2,name:"User #2"},
            {id:3,name:"User #3"},
            {id:4,name:"User #4"}
        ]
    };
    render(){
        return(
            <div className="mainComponent">
                
                <div className="leftSidebar">
                    
                    <div className="leftSidebar_header">
                        <h1>PRED_MOJI</h1>
                    </div>
                    <div className="leftSidebar_threads">
                        {this.state.userArr.map( users => (
                            <UserThreads key={users.id} userArr={users} />
                        ))}
                    </div>
                </div>
                <div className="rightSidebar">
                    <div className="rightSidebar_header">
                        <ChatHeader />
                    </div>
                    <div className="rightSidebar_body">
                        <MessageBody />
                    </div>
                </div>
            </div>
        );
    }

}

export default Main;
